DELIMITER $$
CREATE TRIGGER `after_exam_attempt_complete` AFTER UPDATE ON `exam_attempts` FOR EACH ROW BEGIN
    DECLARE total_points INT DEFAULT 0;
    DECLARE earned_points INT DEFAULT 0;
    DECLARE calculated_score DECIMAL(5,2);
    
    -- Only calculate if status changed to completed
    IF OLD.status != 'completed' AND NEW.status = 'completed' THEN
        -- Get total possible points
        SELECT SUM(points) INTO total_points
        FROM mcq_questions
        WHERE exam_id = NEW.exam_id;
        
        -- Get earned points
        SELECT SUM(q.points) INTO earned_points
        FROM candidate_answers ca
        JOIN mcq_questions q ON ca.question_id = q.id
        WHERE ca.attempt_id = NEW.id AND ca.is_correct = TRUE;
        
        -- Calculate percentage score
        IF total_points > 0 THEN
            SET calculated_score = (earned_points / total_points) * 100;
            
            -- REMOVE THIS PROBLEMATIC UPDATE - Don't update exam_attempts table
            -- UPDATE exam_attempts SET score = calculated_score, correct_answers = ... WHERE id = NEW.id;
            
            -- Only update job application with exam score
            UPDATE job_applications
            SET exam_score = calculated_score
            WHERE candidate_id = NEW.candidate_id
            AND job_id = (SELECT job_id FROM mcq_exams WHERE id = NEW.exam_id);
        END IF;
    END IF;
END
$$
