DELIMITER $$
CREATE TRIGGER `after_job_application_insert` AFTER INSERT ON `job_applications` FOR EACH ROW BEGIN
    DECLARE employer_user_id INT;
    DECLARE job_title VARCHAR(255);
    DECLARE candidate_name VARCHAR(255);
    
    -- Get employer user_id and job title
    SELECT c.user_id, jp.title INTO employer_user_id, job_title
    FROM job_postings jp
    JOIN companies c ON jp.company_id = c.id
    WHERE jp.id = NEW.job_id;
    
    -- Get candidate name
    SELECT CONCAT(u.first_name, ' ', u.last_name) INTO candidate_name
    FROM candidate_profiles cp
    JOIN users u ON cp.user_id = u.id
    WHERE cp.id = NEW.candidate_id;
    
    -- Create notification for employer
    INSERT INTO notifications (user_id, title, message, notification_type)
    VALUES (
        employer_user_id,
        'New Job Application',
        CONCAT('New application received from ', candidate_name, ' for position: ', job_title),
        'application'
    );
    
    -- Log the activity
    INSERT INTO activity_logs (table_name, operation_type, record_id, new_values)
    VALUES ('job_applications', 'INSERT', NEW.id, JSON_OBJECT(
        'job_id', NEW.job_id,
        'candidate_id', NEW.candidate_id,
        'application_status', NEW.application_status
    ));
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `after_job_application_status_update` AFTER UPDATE ON `job_applications` FOR EACH ROW BEGIN
    DECLARE candidate_user_id INT;
    DECLARE job_title VARCHAR(255);
    
    -- Only proceed if status changed
    IF OLD.application_status != NEW.application_status THEN
        -- Get candidate user_id
        SELECT u.id INTO candidate_user_id
        FROM candidate_profiles cp
        JOIN users u ON cp.user_id = u.id
        WHERE cp.id = NEW.candidate_id;
        
        -- Get job title
        SELECT title INTO job_title
        FROM job_postings
        WHERE id = NEW.job_id;
        
        -- Create notification for candidate
        INSERT INTO notifications (user_id, title, message, notification_type)
        VALUES (
            candidate_user_id,
            'Application Status Update',
            CONCAT('Your application for "', job_title, '" has been updated to: ', NEW.application_status),
            'application'
        );
        
        -- Record status change history
        INSERT INTO application_status_history (application_id, old_status, new_status)
        VALUES (NEW.id, OLD.application_status, NEW.application_status);
        
        -- Log the activity
        INSERT INTO activity_logs (table_name, operation_type, record_id, old_values, new_values)
        VALUES ('job_applications', 'UPDATE', NEW.id, 
            JSON_OBJECT('application_status', OLD.application_status),
            JSON_OBJECT('application_status', NEW.application_status)
        );
    END IF;
END
$$
